import React, { useEffect, useState } from "react";
import axios from "axios";

const TaskList = ({ onEdit, onDelete, tasksChanged, onTasksChanged }) => {
  const [tasks, setTasks] = useState([]);

  // Fetch tasks from the backend
  const fetchTasks = () => {
    axios
      .get("http://localhost:3000/tasks")
      .then((response) => {
        setTasks(response.data);
        onTasksChanged(); // Reset the tasksChanged state after tasks are fetched
      })
      .catch((error) => console.error("Error fetching tasks:", error));
  };

  // Re-fetch tasks when the component mounts or when tasksChanged becomes true
  useEffect(() => {
    fetchTasks();
  }, [tasksChanged]);

  return (
    <div>
      <div className="text-center mb-8">
        <Link to="/tasks">
          <button className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600">
            Show Tasks
          </button>
        </Link>
      </div>
      <div className="p-6 bg-gray-100">
        <h2 className="text-xl font-bold mb-4">Task List</h2>
        <ul className="space-y-4">
          {tasks.map((task) => (
            <li
              key={task.id}
              className="p-4 bg-white rounded shadow-md flex justify-between"
            >
              <div>
                <h3 className="font-semibold">{task.title}</h3>
                <p>{task.description}</p>
              </div>
              <div>
                <button
                  className="mr-2 px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
                  onClick={() => onEdit(task)}
                >
                  Edit
                </button>
                <button
                  className="px-3 py-1 bg-red-500 text-white rounded hover:bg-red-600"
                  onClick={() => onDelete(task.id)}
                >
                  Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default TaskList;
