import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import TaskList from "./TaskList";
import TaskForm from "./TaskForm";
import axios from "axios";

const App = () => {
  const [editingTask, setEditingTask] = useState(null);
  const [successMessage, setSuccessMessage] = useState(""); // Message for task deletion or addition

  const refreshTasks = () => {
    setEditingTask(null);
  };

  const handleEdit = (task) => {
    setEditingTask(task);
  };

  const handleDelete = (id) => {
    axios
      .delete(`http://localhost:3000/tasks/${id}`)
      .then(() => {
        refreshTasks(); // Refresh the list after deletion
        setSuccessMessage("Task deleted successfully!"); // Set success message for deletion
        setTimeout(() => setSuccessMessage(""), 3000); // Clear message after 3 seconds
      })
      .catch((error) => console.error("Error deleting task:", error));
  };

  return (
    <Router>
      <div className="container mx-auto p-6">
        <h1 className="text-3xl font-bold text-center mb-8">Task Management</h1>

        {/* Display success message if a task is added or deleted */}
        {successMessage && (
          <div className="mb-4 p-2 bg-green-100 text-green-700 rounded text-center">
            {successMessage}
          </div>
        )}

        {/* Add a "Show Tasks" button that links to /tasks */}
        <div className="text-center mb-8">
          <Link to="/tasks">
            <button className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600">
              Show Tasks
            </button>
          </Link>
        </div>

        {/* Define routes for TaskForm and TaskList */}
        <Routes>
          {/* Route for the task form (default route) */}
          <Route
            path="/"
            element={
              <TaskForm
                currentTask={editingTask}
                onTaskSubmit={() => {
                  refreshTasks();
                  setSuccessMessage("Task added/updated successfully!"); // Set success message for add/update
                  setTimeout(() => setSuccessMessage(""), 3000); // Clear after 3 seconds
                }}
              />
            }
          />

          {/* Route for the task list */}
          <Route
            path="/tasks"
            element={<TaskList onEdit={handleEdit} onDelete={handleDelete} />}
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
